package com.pool;

public class Tweet {
	private String name;
	private String tweet;
	public Tweet() {
		super();
		// TODO Auto-generated constructor stub
	}
	public String getName() {
		return name;
	}
	public Tweet setName(String name) {
		this.name = name;
		return this;
	}
	public String getTweet() {
		return tweet;
	}
	public Tweet setTweet(String tweet) {
		this.tweet = tweet;
		return this;
	}
	
}
